package us.hall.osx;import java.io.*; /*  	OutputStream wrapping NSLog*/ public class LogOut extends PrintStream implements Serializable { 	static { System.loadLibrary("logio"); } 	private final static LogOut instance = new LogOut(new ByteArrayOutputStream()); 		private static native void logPrint(boolean b); 	private static native void logPrint(char c); 	private static native void logPrint(int i); 	private static native void logPrint(long l); 	private static native void logPrint(float f); 	private static native void logPrint(double d); 	private static native void logPrint(String s);			public LogOut(OutputStream os) {		super(os);	}		public static LogOut getInstance() {		return instance;	}		/* 	  * provide a dummy close 	  * Should set a boolean all routines check and don't do anything	  * for proper close behavior?	  */	public void close() { 	}		public void print(boolean b) 	{ 		logPrint(b);	}				public void print(char c)	{		logPrint(c);	}		public void print(int i)	{		logPrint(i);	}		public void print(long l)	{		logPrint(l);	}	public void print(float f)	{		logPrint(f);	}		public void print(double d)	{		logPrint(d);	}		public void print(char [] s)	{		logPrint(new String(s));	}		public void print(String s)	{		logPrint(s);	}		public void print(Object o)	{		print(o.toString());	}		public void println(boolean b) 	{ 		print(b);	}				public void println(char c)	{		print(c);	}		public void println(int i)	{		print(i);	}		public void println(long l)	{		print(l);	}	public void println(float f)	{		print(f);	}		public void println(double d)	{		print(d);	}		public void println(char [] s)	{		print(s);	}					public void println()	{		print("");	}		public void println(String s) 	{		print(s);	}	public void println(Object o)	{		print(o);	}			public void write(int b) 	{		print((char)b);	}			public void write(byte[] b, int o, int l)	{		print(new String(b,o,l));			}	public void flush() {	}}	